import "./App.css";
import { useState, useEffect } from "react";

function App() {

  function myFunction() {
    let text;
    let person = prompt("Please enter your name:", "Username");
    let password = prompt("Please enter your name:", "Username");
    if (person == null || person == "") {
      setMyArray([...myArray, inputValue, "Acess denied"]);
    } else {
      if (person == "enzo" && password == "1234"){
        setMyArray([...myArray, person + " Authentified! "]);
        user1auth = true
      }else{
        setMyArray([...myArray, person + " Is not in the user list, Aborting.. "]);
        document.cookie = "authuser1=true; path=/"
      }
    }
    document.getElementById("demo").innerHTML = text;
  }

useEffect(()=>{
  let x = document.cookie;
  console.log(x)
  if (x == true) {
    setMyArray([...myArray, "Welcome back Enzo !"]);
  } else {
    setMyArray([...myArray, "Its fun to watch them play"]);
  }
}, [])
  const user1auth = false;
  const [inputValue, setInputValue] = useState("");
  const [myArray, setMyArray] = useState([]);

  const handleInput = (e) => {
    setInputValue(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (inputValue === "delete") {
      setMyArray([...myArray,"/>" + inputValue, "Acess denied"]);
    } else if (inputValue === "clear") {
      setMyArray([]);
    }
  else if (inputValue === "reset") {
    window.location.reload(false);
  }
  else if (inputValue === "unauth") {
    document.cookie = "authuser1=false; path=/";
    window.location.reload(false);
  }
  else if (inputValue === "auth") {
    setMyArray([...myArray,"/>" + inputValue]);
    myFunction()
  }
  else if (inputValue === "help") {
    setMyArray([...myArray,"/>" + inputValue, "List of help", "reset - Resets page / console", "clear - Clears the console", "auth - Identify yourself to gain acess to some of the console commands", "delete - [AUTH REQUIRED] Deletes a file", "help - Shows this help"]);
  }
    else {
      setMyArray([...myArray,"/>" + inputValue, inputValue + " is not a function or a file" ]);
    }
    setInputValue([]);
  };


  return (
    <div className="App">
      <div className="Console"></div>
      <div className="children">
        {myArray.map((item, i) => (
          <p key={i}>{item}</p>
        ))}

        <form
          onSubmit={(e) => {
            handleSubmit(e);
          }}
        >
   /><input id="input" autocomplete="off" value={inputValue} onChange={(e) => handleInput(e)} autofocus></input> 
        </form>
      </div>
    </div>
  );
}

export default App;
