import "./App.css";
import { useState } from "react";

function App() {

  function myFunction() {
    let text;
    let person = prompt("Please enter your name:", "Harry Potter");
    if (person == null || person == "") {
      text = "User cancelled the prompt.";
    } else {
      text = "Hello " + person + "! How are you today?";
    }
    document.getElementById("demo").innerHTML = text;
  }

  const user1username = "enzo";
  const user1password = "1234";
  const user1auth = false;
  const [inputValue, setInputValue] = useState("aucune entrée");
  const [myArray, setMyArray] = useState([]);

  const handleInput = (e) => {
    setInputValue(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (inputValue === "delete") {
      setMyArray([...myArray, inputValue, "Acess denied"]);
    } else if (inputValue === "clear") {
      setMyArray([]);
    }
  else if (inputValue === "reset") {
    window.location.reload(false);
  }
  else if (inputValue === "auth") {
    myFunction()
  }
    else {
      setMyArray([...myArray, inputValue + " is not a function or a file" ]);
    }
    setInputValue([]);
  };

  return (
    <div className="App">
      <div className="children">
        {myArray.map((item, i) => (
          <p key={i}>{item}</p>
        ))}

        <form
          onSubmit={(e) => {
            handleSubmit(e);
          }}
        >
          <input value={inputValue} onChange={(e) => handleInput(e)}></input>
        </form>
      </div>
    </div>
  );
}

export default App;
