import "./App.css";
import { useState } from "react";

function App() {
  const [inputValue, setInputValue] = useState("aucune entrée");
  const [myArray, setMyArray] = useState([]);

  const handleInput = (e) => {
    setInputValue(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (inputValue === "delete" || "Delete" || "DELETE") {
      setMyArray([...myArray, inputValue, "Acess denied"]);
    } else if (inputValue === "clear") {
      setMyArray([]);
    }
  else if (inputValue === "reset") {
    window.location.reload(false);
  }
    else {
      setMyArray([...myArray, inputValue]);
    }
    setInputValue([]);
  };

  return (
    <div className="App">
      <div className="children">
        {myArray.map((item, i) => (
          <p key={i}>{item}</p>
        ))}

        <form
          onSubmit={(e) => {
            handleSubmit(e);
          }}
        >
          <input value={inputValue} onChange={(e) => handleInput(e)}></input>
        </form>
      </div>
    </div>
  );
}

export default App;
